// Get data from previous nodes
const agentConfig = $('Get Agent Config + Branch_id').first().json;
const webhookData = $('Webhook').first().json.body;

// Handle both direct body and nested body scenarios
const data = webhookData || $('Webhook').first().json;

const fileName = data.files?.[0]?.name || '';
const content = data.content || '';
const action = data.action || '';

// Validate we have required data
if (!fileName || !content) {
  return [{
    json: {
      skip: true,
      reason: 'Missing required data: fileName or content',
      receivedData: { fileName, hasContent: !!content, action }
    }
  }];
}

// Only process 'update' or 'revert' actions
if (action !== 'update' && action !== 'revert') {
  return [{
    json: {
      skip: true,
      reason: `Unhandled action: ${action}. Only 'update' and 'revert' are supported.`,
      action
    }
  }];
}

// Normalize filename for comparison - remove .md if present, lowercase, trim
const normalizedFileName = fileName.toLowerCase().trim().replace(/\.md$/, '');

// Search through all workflow nodes for this document
const workflowNodes = agentConfig.workflow?.nodes || {};
const matchingNodes = [];

for (const [nodeId, nodeConfig] of Object.entries(workflowNodes)) {
  const kb = nodeConfig.additional_knowledge_base || [];

  // Find matching document - normalize both names for comparison
  const matchingDoc = kb.find(doc => {
    const normalizedDocName = (doc.name || '').toLowerCase().trim().replace(/\.md$/, '');
    // Check exact match (after normalization) OR partial match
    return normalizedDocName === normalizedFileName ||
           normalizedDocName.includes(normalizedFileName) || 
           normalizedFileName.includes(normalizedDocName);
  });

  if (matchingDoc) {
    matchingNodes.push({
      nodeId,
      nodeLabel: nodeConfig.label || nodeId,
      existingDocId: matchingDoc.id,
      existingDocName: matchingDoc.name,
      fullKbArray: kb
    });
  }
}

// If document not found in any node, return skip flag
if (matchingNodes.length === 0) {
  return [{
    json: {
      skip: true,
      reason: `Document "${fileName}" not found in any sub-agent KB. Document must already exist in ElevenLabs for automatic updates.`,
      fileName,
      normalizedFileName,
      searchedNodes: Object.keys(workflowNodes).length
    }
  }];
}

// Log success info
console.log(`Found document "${fileName}" in ${matchingNodes.length} node(s)`);
matchingNodes.forEach(n => console.log(`  - ${n.nodeLabel} (${n.nodeId}) matched "${n.existingDocName}"`));

return [{
  json: {
    skip: false,
    fileName,
    content,
    action,
    matchingNodes,
    agentConfig
  }
}];

// Get data from Find Document node
const data = $('Find Document in Agent').first().json;
const content = data.content;
const fileName = data.fileName;

// Validate we have content
if (!content) {
  throw new Error('No content available to convert to binary');
}

// Create binary data from text content
const binaryData = Buffer.from(content, 'utf-8');

return [{
  json: {
    fileName: fileName,
    matchingNodes: data.matchingNodes,
    agentConfig: data.agentConfig,
    action: data.action
  },
  binary: {
    file: {
      data: binaryData.toString('base64'),
      mimeType: 'text/markdown',
      fileName: fileName
    }
  }
}];

// Get data from previous nodes
const findData = $('Find Document in Agent').first().json;
const uploadResult = $('Upload New Document').first().json;
const branchId = $('Add Branch_id').first().json.branch_id;
const agentConfig = $('Get Agent Config + Branch_id').first().json;

// Get the matching node info
const matchingNode = findData.matchingNodes[0];
const targetNodeId = matchingNode.nodeId;
const existingDocId = matchingNode.existingDocId;
const existingDocName = matchingNode.existingDocName;
const fullKbArray = matchingNode.fullKbArray;

// Get the new document info from upload response
const newDocId = uploadResult.id;
const newDocName = existingDocName;

// Build the updated KB array
const updatedKbArray = [
  ...fullKbArray.filter(doc => doc.id !== existingDocId),
  {
    type: 'file',
    name: newDocName,
    id: newDocId,
    usage_mode: 'auto'
  }
];

// Get the FULL workflow from agent config
const fullWorkflow = JSON.parse(JSON.stringify(agentConfig.workflow));

// Update ONLY the target node's additional_knowledge_base
fullWorkflow.nodes[targetNodeId].additional_knowledge_base = updatedKbArray;

// Build PATCH payload with the COMPLETE workflow
const patchPayload = {
  workflow: fullWorkflow
};

return [{
  json: {
    patchPayload,
    branchId,
    debug: {
      newDocId,
      newDocName,
      targetNodeId,
      targetNodeLabel: matchingNode.nodeLabel,
      removedDocId: existingDocId,
      previousDocCount: fullKbArray.length,
      newDocCount: updatedKbArray.length,
      totalNodesInWorkflow: Object.keys(fullWorkflow.nodes).length
    }
  }
}];

